package com.acc.lkm.JavaDay2;

public class Stringfunc {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

				// TODO Auto-generated method stub
				String str=new String("JavaSchool");
				System.out.println(str.indexOf("o"));
				System.out.println(str.lastIndexOf("o"));
				System.out.println(str.replace("o","s"));
				System.out.println(str.toUpperCase());
				System.out.println(str.substring(3,7));
				System.out.println(str.equalsIgnoreCase("Gourab"));
				System.out.println(str.endsWith("l"));
				System.out.println(str.isEmpty());
				System.out.println(str.concat(" is Accenture"));
			


	}

}
